import React from 'react';
import { Card, Col, Image, Row, Skeleton } from 'antd';
import { isEmpty } from 'lodash';
import PropTypes from 'prop-types';
import styled from 'styled-components';
import If from '../If';
import { T } from '../T';
import * as colors from '@app/themes/colors';

const StyledCol = styled(Col)`
  && {
    height: 100%;
  }
`;

const StyledTrackCard = styled(Card)`
  && {
    height: 100%;
  }
`;

const StyledImage = styled(Image)`
  && {
    width: 100px;
    height: 100px;
    border-radius: 50%;

    @media screen and (max-width: 400px) {
      width: 80px;
      height: 80px;
    }
  }
`;

const TrackName = styled(T)`
  && {
    font-size: 1rem;
    color: ${colors.success};
  }
`;
const TrackCollectionName = styled(T)``;
const TrackPrice = styled(T)`
  && {
    color: ${colors.error};
  }
`;
const TrackGenre = styled(T)``;

function TrackCard({ skeletonLoading, trackName, collectionName, artworkUrl100, trackPrice, primaryGenre }) {
  return (
    <Skeleton loading={skeletonLoading} active>
      <StyledCol md={12} lg={8} style={{ height: '100%' }}>
        <StyledTrackCard hoverable>
          <Row gutter={48} align="middle">
            <Col span={8}>
              <StyledImage
                preview={false}
                src={artworkUrl100}
                fallback="data:image/png;base64,"
              />
            </Col>
            <Col span={16}>
              <If condition={!isEmpty(trackName)} otherwise={<T id="track_name_unavailable" />}>
                <TrackName type="subheading" text={trackName} />
              </If>
              <TrackCollectionName type="subText" text={collectionName} />
              <TrackPrice text={String(trackPrice)} />
              <TrackGenre text={primaryGenre} />
            </Col>
          </Row>
        </StyledTrackCard>
      </StyledCol>
    </Skeleton>
  );
}

TrackCard.propTypes = {
  skeletonLoading: PropTypes.bool,
  trackId: PropTypes.number,
  collectionName: PropTypes.string,
  trackName: PropTypes.string,
  artworkUrl100: PropTypes.string,
  trackPrice: PropTypes.number,
  releaseDate: PropTypes.string,
  primaryGenre: PropTypes.string
};

export default TrackCard;
